mapper.select:
select * from ( SELECT m.id AS id, m.kor_name AS kor_name, m.eng_name AS eng_name, m.price AS price, m.img AS img, m.reg_date AS reg_date, m.category_id AS category_id, COUNT(ml.menu_id) AS like_count
, nvl(ml.member_id=${memberId},0) AS `like`
FROM (menu m LEFT JOIN menu_like ml ON (m.id = ml.menu_id)) GROUP BY m.id ) mv
mv.category_id = ${categoryId}
and mv.kor_name like '%${query}%'
order by mv.reg_date desc LIMIT #{offset}, #{size}

select count(id) from menu_view
category_id = #{categoryId}
kor_name like '%${query}%'
